    // Create a concrete view (or any other Backbone EventEmitter)
    view = new View();
// Subscribe to the event when both `foo` and `bar` resolved regardless the order they fired
view.all( [ "foo", "bar" ], function( tasks ){
 console.log( "`foo` and `bar` both resolved" );
 console.log( "`foo` emitted with parameters", tasks.foo );
 console.log( "`bar` emitted with parameters", tasks.bar );
});
// Fire the events
setTimeout(function(){
 console.log( "`bar` fired" );
 view.trigger( "bar", "bar" );
}, 100 );

console.log( "`foo` fired" );
view.trigger( "foo", "foo" );

</script>
